import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import TutorSearch from './components/TutorSearch';
import TutorProfile from './components/TutorProfile';
import Register from './pages/Register';
import Login from './pages/Login';
import StudentDashboard from './pages/StudentDashboard';
import TutorApplication from './components/TutorApplication';
import TutorDashboard from './pages/TutorDashboard';
import { AuthProvider } from './context/AuthContext';
import PrivateRoute from './components/PrivateRoute';

function App() {
  return (
    <AuthProvider>
      <Router>
        {/*
          Removed Tailwind classes. This div will be unstyled by default.
          Its height and layout will depend on its children and default browser styling.
          We will use Chakra UI for layout and styling WITHIN pages.
        */}
        <div>
          <Navbar />
          {/*
            Removed Tailwind classes. This main element will also be unstyled.
            Its growth behavior will need to be managed by default CSS or
            Chakra's Box component with flex properties if you want a sticky footer.
          */}
          <main>
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<Home />} />
              <Route path="/search" element={<TutorSearch />} />
              <Route path="/tutors/:id" element={<TutorProfile />} />
              <Route path="/register" element={<Register />} />
              <Route path="/login" element={<Login />} />

              {/* Private Routes - Student */}
              <Route
                path="/student/dashboard"
                element={<PrivateRoute allowedRoles={['student']} />}
              >
                <Route index element={<StudentDashboard />} />
              </Route>

              {/* Private Routes - Tutor */}
              <Route
                path="/tutor/dashboard"
                element={<PrivateRoute allowedRoles={['tutor']} />}
              >
                <Route index element={<TutorDashboard />} />
              </Route>

              {/* Private Route - Tutor Application */}
              <Route
                path="/apply"
                element={<PrivateRoute allowedRoles={['student', 'tutor']} />}
              >
                <Route index element={<TutorApplication />} />
              </Route>

              {/* Catch-all route for 404 */}
              <Route path="*" element={<p style={{ textAlign: 'center', marginTop: '50px', fontSize: '20px' }}>404: Page Not Found</p>} />

            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
