// client/src/index.js

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App'; // Your main App component
import reportWebVitals from './reportWebVitals';

// 1. Import ChakraProvider and extendTheme
import { ChakraProvider, extendTheme } from '@chakra-ui/react';

// Optional: Define a simple custom theme (you can expand this later)
const customTheme = extendTheme({
  colors: {
    brand: {
      50: "#e6f0ff",
      100: "#c0d7ff",
      200: "#99beff",
      300: "#72a4ff",
      400: "#4b8aff",
      500: "#2470ff", // Primary blue for your app
      600: "#1e5acc",
      700: "#184599",
      800: "#123066",
      900: "#0c1b33",
    },
  },
  // You can add fonts, breakpoints, etc. here
  fonts: {
    heading: 'Inter, sans-serif',
    body: 'Inter, sans-serif',
  },
});

// IMPORTANT: We need to make sure index.css is NOT imported here
// if you previously moved its content to App.js or another file
// If you still have global CSS in index.css (like your custom layers or Inter font import)
// AND it's NOT Tailwind related, you might need to keep a clean version of it.
// For now, let's assume all global styling will be handled by Chakra.
// If you have non-Tailwind global styles you want to keep, you'll need to adapt.
// For now, let's prioritize a clean start with Chakra.
// If you moved all App.css content into index.css and then deleted App.css,
// and index.css still imported @tailwind, then index.css should now be gone.
// If you have a clean index.css with ONLY font imports or very basic resets, it might stay.
// Let's assume for now we are starting fresh with styling via Chakra.

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    {/* 2. Wrap your App with ChakraProvider and pass the theme */}
    <ChakraProvider theme={customTheme}>
      <App />
    </ChakraProvider>
  </React.StrictMode>
);

reportWebVitals();
