import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import {
  Box,
  Flex,
  HStack,
  VStack,
  Text,
  Button,
  IconButton,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  Badge,
  Container,
  Collapse,
  useDisclosure,
  useColorModeValue,
  Spacer,
  Divider,
  useBreakpointValue,
  Portal,
  Slide,
  ScaleFade,
  Tooltip,
} from '@chakra-ui/react';
import { 
  Search, 
  Menu as MenuIcon, 
  X, 
  User, 
  Bell, 
  Settings, 
  LogOut, 
  BookOpen, 
  Users, 
  Award, 
  ChevronDown 
} from 'lucide-react';

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const { isOpen: isMenuOpen, onToggle: toggleMenu, onClose: closeMenu } = useDisclosure();
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  // Responsive values
  const isMobile = useBreakpointValue({ base: true, lg: false });
  const navHeight = useBreakpointValue({ base: '72px', lg: '80px' });

  // Color mode values
  const bgColor = useColorModeValue(
    scrolled || location.pathname !== '/' ? 'rgba(255, 255, 255, 0.9)' : 'transparent',
    scrolled || location.pathname !== '/' ? 'rgba(26, 32, 44, 0.9)' : 'transparent'
  );

  const textColor = useColorModeValue(
    scrolled || location.pathname !== '/' ? 'gray.700' : 'white',
    scrolled || location.pathname !== '/' ? 'white' : 'gray.100'
  );

  const hoverBg = useColorModeValue('purple.50', 'whiteAlpha.100');
  const borderColor = useColorModeValue('purple.100', 'whiteAlpha.200');

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    closeMenu();
  }, [location, closeMenu]);

  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  const handleLogout = () => {
    logout();
  };

  // Custom gradient backgrounds
  const gradientBg = 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
  const logoGradient = 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';

  // Navigation items for desktop
  const NavItem = ({ to, icon: Icon, children, isActive, ...props }) => (
    <Button
      as={Link}
      to={to}
      variant={isActive ? 'solid' : 'ghost'}
      bg={isActive ? gradientBg : 'whiteAlpha.100'}
      color={isActive ? 'white' : textColor}
      _hover={{
        bg: isActive ? gradientBg : hoverBg,
        color: isActive ? 'white' : 'purple.600',
        transform: 'translateY(-1px)',
      }}
      backdropFilter="blur(10px)"
      border="1px solid"
      borderColor={borderColor}
      borderRadius="xl"
      fontWeight="semibold"
      fontSize="sm"
      px={4}
      py={2.5}
      transition="all 0.3s ease"
      leftIcon={Icon && <Icon size={16} />}
      {...props}
    >
      {children}
    </Button>
  );

  // Mobile menu item
  const MobileNavItem = ({ to, icon: Icon, title, subtitle, isActive }) => (
    <Box
      as={Link}
      to={to}
      display="block"
      p={5}
      bg={isActive ? gradientBg : 'transparent'}
      color={isActive ? 'white' : 'gray.700'}
      borderRadius="2xl"
      transition="all 0.3s ease"
      _hover={{
        bg: isActive ? gradientBg : 'purple.50',
        color: isActive ? 'white' : 'purple.700',
        textDecoration: 'none',
      }}
    >
      <HStack spacing={4}>
        <Box
          w={10}
          h={10}
          bg={isActive ? 'whiteAlpha.200' : 'purple.100'}
          borderRadius="xl"
          display="flex"
          alignItems="center"
          justifyContent="center"
        >
          <Icon size={20} />
        </Box>
        <VStack align="start" spacing={0}>
          <Text fontWeight="bold" fontSize="md">{title}</Text>
          <Text fontSize="sm" opacity={0.75}>{subtitle}</Text>
        </VStack>
      </HStack>
    </Box>
  );

  return (
    <>
      <Box
        position="fixed"
        top={0}
        left={0}
        right={0}
        zIndex={50}
        bg={bgColor}
        backdropFilter={scrolled || location.pathname !== '/' ? 'blur(20px)' : 'none'}
        boxShadow={scrolled || location.pathname !== '/' ? '2xl' : 'none'}
        borderBottom="1px solid"
        borderBottomColor={scrolled || location.pathname !== '/' ? borderColor : 'transparent'}
        transition="all 0.5s ease"
      >
        <Container maxW="container.xl" px={6}>
          <Flex justify="space-between" align="center" h={navHeight}>
            {/* Enhanced Logo */}
            <Flex as={Link} to="/" align="center" spacing={3} _hover={{ transform: 'scale(1.05)' }} transition="all 0.3s ease">
              <Box position="relative">
                <Box
                  bg={logoGradient}
                  p={3}
                  borderRadius="2xl"
                  boxShadow="lg"
                  _hover={{ boxShadow: 'xl' }}
                  transition="all 0.3s ease"
                >
                  <BookOpen size={28} color="white" />
                </Box>
                <Box
                  position="absolute"
                  top="-4px"
                  right="-4px"
                  w={3}
                  h={3}
                  bg="linear-gradient(45deg, #FED7AA, #FB923C)"
                  borderRadius="full"
                  animation="pulse 2s infinite"
                />
              </Box>
              <VStack align="start" spacing={0} ml={3}>
                <Text
                  fontSize="2xl"
                  fontWeight="black"
                  bgGradient="linear(to-r, purple.600, blue.600, indigo.700)"
                  bgClip="text"
                >
                  TutorMatch
                </Text>
                <Text
                  fontSize="xs"
                  fontWeight="medium"
                  color={scrolled || location.pathname !== '/' ? 'purple.500' : 'purple.200'}
                  mt="-1"
                  transition="color 0.3s ease"
                >
                  Learn • Teach • Grow
                </Text>
              </VStack>
            </Flex>

            {/* Desktop Navigation */}
            {!isMobile && (
              <HStack spacing={2}>
                <HStack 
                  bg="whiteAlpha.100" 
                  backdropFilter="blur(10px)" 
                  borderRadius="2xl" 
                  p={1} 
                  border="1px solid" 
                  borderColor={borderColor}
                  spacing={0}
                >
                  <NavItem 
                    to="/search" 
                    icon={Search} 
                    isActive={isActiveLink('/search')}
                  >
                    Find Tutors
                  </NavItem>
                  <NavItem 
                    to="/apply" 
                    icon={Users} 
                    isActive={isActiveLink('/apply')}
                  >
                    Become Tutor
                  </NavItem>
                </HStack>

                {currentUser ? (
                  <HStack spacing={3} ml={4}>
                    {/* Dashboard Link */}
                    <NavItem
                      to={currentUser.role === 'tutor' ? '/tutor-dashboard' : '/student-dashboard'}
                      icon={BookOpen}
                      isActive={isActiveLink('/tutor-dashboard') || isActiveLink('/student-dashboard')}
                    >
                      Dashboard
                    </NavItem>

                    {/* Notifications */}
                    <Tooltip label="You have 3 new notifications" placement="bottom">
                      <IconButton
                        icon={<Bell size={20} />}
                        variant="ghost"
                        bg="whiteAlpha.100"
                        backdropFilter="blur(10px)"
                        border="1px solid"
                        borderColor={borderColor}
                        borderRadius="xl"
                        color={textColor}
                        _hover={{
                          bg: hoverBg,
                          color: 'purple.600',
                          transform: 'translateY(-1px)',
                        }}
                        position="relative"
                        transition="all 0.3s ease"
                      >
                        <Badge
                          position="absolute"
                          top="-1"
                          right="-1"
                          bg="linear-gradient(45deg, #EF4444, #EC4899)"
                          color="white"
                          fontSize="xs"
                          borderRadius="full"
                          minW={4}
                          h={4}
                          display="flex"
                          alignItems="center"
                          justifyContent="center"
                          animation="bounce 1s infinite"
                        >
                          3
                        </Badge>
                      </IconButton>
                    </Tooltip>

                    {/* Profile Dropdown */}
                    <Menu>
                      <MenuButton
                        as={Button}
                        variant="ghost"
                        bg="whiteAlpha.100"
                        backdropFilter="blur(10px)"
                        border="1px solid"
                        borderColor={borderColor}
                        borderRadius="xl"
                        color={textColor}
                        _hover={{
                          bg: hoverBg,
                          color: 'purple.600',
                        }}
                        _active={{
                          bg: hoverBg,
                        }}
                        px={4}
                        py={2.5}
                        transition="all 0.3s ease"
                        rightIcon={<ChevronDown size={16} />}
                      >
                        <HStack spacing={3}>
                          <Box position="relative">
                            <Avatar
                              size="sm"
                              name={currentUser.name || 'User'}
                              bg={logoGradient}
                              color="white"
                              fontWeight="bold"
                            />
                            <Box
                              position="absolute"
                              bottom="-2px"
                              right="-2px"
                              w={3}
                              h={3}
                              bg="green.400"
                              borderRadius="full"
                              border="2px solid white"
                            />
                          </Box>
                          <VStack align="start" spacing={0} display={{ base: 'none', xl: 'flex' }}>
                            <Text fontWeight="semibold" fontSize="sm">
                              {currentUser.name?.split(' ')[0] || 'User'}
                            </Text>
                            <Text fontSize="xs" opacity={0.75} textTransform="capitalize">
                              {currentUser.role}
                            </Text>
                          </VStack>
                        </HStack>
                      </MenuButton>
                      <Portal>
                        <MenuList
                          bg="rgba(255, 255, 255, 0.95)"
                          backdropFilter="blur(20px)"
                          borderRadius="3xl"
                          boxShadow="2xl"
                          border="1px solid"
                          borderColor={borderColor}
                          py={3}
                          minW="288px"
                        >
                          {/* Profile Header */}
                          <Box px={6} py={4} borderBottom="1px solid" borderBottomColor={borderColor}>
                            <HStack spacing={4}>
                              <Avatar
                                size="md"
                                name={currentUser.name || 'User'}
                                bg={logoGradient}
                                color="white"
                                fontWeight="bold"
                              />
                              <VStack align="start" spacing={0}>
                                <Text fontWeight="bold" color="gray.900">
                                  {currentUser.name || 'User'}
                                </Text>
                                <Text fontSize="sm" color="gray.600">
                                  {currentUser.email}
                                </Text>
                                <Badge
                                  bg={currentUser.role === 'tutor' ? 'purple.100' : 'green.100'}
                                  color={currentUser.role === 'tutor' ? 'purple.700' : 'green.700'}
                                  fontSize="xs"
                                  fontWeight="semibold"
                                  px={2}
                                  py={1}
                                  borderRadius="full"
                                  mt={1}
                                >
                                  {currentUser.role === 'tutor' ? '👨‍🏫 Tutor' : '👨‍🎓 Student'}
                                </Badge>
                              </VStack>
                            </HStack>
                          </Box>

                          {/* Menu Items */}
                          <Box py={2}>
                            <MenuItem
                              as={Link}
                              to="/profile"
                              px={6}
                              py={3}
                              _hover={{ bg: 'purple.50', color: 'purple.700' }}
                              transition="all 0.2s ease"
                            >
                              <HStack spacing={4}>
                                <Box
                                  w={8}
                                  h={8}
                                  bg="purple.100"
                                  borderRadius="xl"
                                  display="flex"
                                  alignItems="center"
                                  justifyContent="center"
                                >
                                  <User size={16} color="#7C3AED" />
                                </Box>
                                <Text fontWeight="medium">My Profile</Text>
                              </HStack>
                            </MenuItem>
                            <MenuItem
                              as={Link}
                              to="/settings"
                              px={6}
                              py={3}
                              _hover={{ bg: 'purple.50', color: 'purple.700' }}
                              transition="all 0.2s ease"
                            >
                              <HStack spacing={4}>
                                <Box
                                  w={8}
                                  h={8}
                                  bg="purple.100"
                                  borderRadius="xl"
                                  display="flex"
                                  alignItems="center"
                                  justifyContent="center"
                                >
                                  <Settings size={16} color="#7C3AED" />
                                </Box>
                                <Text fontWeight="medium">Settings</Text>
                              </HStack>
                            </MenuItem>
                            {currentUser.role === 'tutor' && (
                              <MenuItem
                                as={Link}
                                to="/earnings"
                                px={6}
                                py={3}
                                _hover={{ bg: 'orange.50', color: 'orange.700' }}
                                transition="all 0.2s ease"
                              >
                                <HStack spacing={4}>
                                  <Box
                                    w={8}
                                    h={8}
                                    bg="orange.100"
                                    borderRadius="xl"
                                    display="flex"
                                    alignItems="center"
                                    justifyContent="center"
                                  >
                                    <Award size={16} color="#EA580C" />
                                  </Box>
                                  <Text fontWeight="medium">Earnings</Text>
                                </HStack>
                              </MenuItem>
                            )}
                          </Box>

                          <MenuDivider />

                          <MenuItem
                            onClick={handleLogout}
                            px={6}
                            py={3}
                            color="red.600"
                            _hover={{ bg: 'red.50' }}
                            transition="all 0.2s ease"
                          >
                            <HStack spacing={4}>
                              <Box
                                w={8}
                                h={8}
                                bg="red.100"
                                borderRadius="xl"
                                display="flex"
                                alignItems="center"
                                justifyContent="center"
                              >
                                <LogOut size={16} color="#DC2626" />
                              </Box>
                              <Text fontWeight="medium">Sign Out</Text>
                            </HStack>
                          </MenuItem>
                        </MenuList>
                      </Portal>
                    </Menu>
                  </HStack>
                ) : (
                  <HStack spacing={3} ml={4}>
                    <Button
                      as={Link}
                      to="/login"
                      variant={isActiveLink('/login') ? 'solid' : 'ghost'}
                      bg={isActiveLink('/login') ? gradientBg : 'whiteAlpha.100'}
                      color={isActiveLink('/login') ? 'white' : textColor}
                      _hover={{
                        bg: isActiveLink('/login') ? gradientBg : hoverBg,
                        color: isActiveLink('/login') ? 'white' : 'purple.600',
                      }}
                      backdropFilter="blur(10px)"
                      border="1px solid"
                      borderColor={borderColor}
                      borderRadius="xl"
                      fontWeight="semibold"
                      fontSize="sm"
                      px={5}
                      py={2.5}
                      transition="all 0.3s ease"
                    >
                      Sign In
                    </Button>
                    <Button
                      as={Link}
                      to="/register"
                      bg={gradientBg}
                      color="white"
                      _hover={{
                        bg: gradientBg,
                        transform: 'translateY(-2px) scale(1.05)',
                      }}
                      borderRadius="xl"
                      fontWeight="bold"
                      fontSize="sm"
                      px={6}
                      py={2.5}
                      boxShadow="xl"
                      _active={{
                        transform: 'translateY(-1px) scale(1.02)',
                      }}
                      transition="all 0.3s ease"
                    >
                      Get Started Free
                    </Button>
                  </HStack>
                )}
              </HStack>
            )}

            {/* Mobile Menu Button */}
            {isMobile && (
              <IconButton
                icon={isMenuOpen ? <X size={24} /> : <MenuIcon size={24} />}
                variant="ghost"
                bg="whiteAlpha.100"
                backdropFilter="blur(10px)"
                border="1px solid"
                borderColor={borderColor}
                borderRadius="xl"
                color={textColor}
                _hover={{
                  bg: hoverBg,
                }}
                onClick={toggleMenu}
                transition="all 0.3s ease"
              />
            )}
          </Flex>
        </Container>

        {/* Mobile Menu */}
        <Collapse in={isMenuOpen} animateOpacity>
          <Box
            bg="rgba(255, 255, 255, 0.95)"
            backdropFilter="blur(20px)"
            borderTop="1px solid"
            borderTopColor={borderColor}
          >
            <Container maxW="container.xl" px={6} py={6}>
              <VStack spacing={3} align="stretch">
                <MobileNavItem
                  to="/search"
                  icon={Search}
                  title="Find Tutors"
                  subtitle="Discover expert tutors"
                  isActive={isActiveLink('/search')}
                />
                <MobileNavItem
                  to="/apply"
                  icon={Users}
                  title="Become a Tutor"
                  subtitle="Start teaching today"
                  isActive={isActiveLink('/apply')}
                />

                {currentUser ? (
                  <>
                    <MobileNavItem
                      to={currentUser.role === 'tutor' ? '/tutor-dashboard' : '/student-dashboard'}
                      icon={BookOpen}
                      title="Dashboard"
                      subtitle="Your control center"
                      isActive={isActiveLink('/tutor-dashboard') || isActiveLink('/student-dashboard')}
                    />

                    <Divider />

                    {/* User Profile Section */}
                    <Box p={5} bg="purple.50" borderRadius="2xl">
                      <HStack spacing={4}>
                        <Avatar
                          size="lg"
                          name={currentUser.name || 'User'}
                          bg={logoGradient}
                          color="white"
                          fontWeight="bold"
                        />
                        <VStack align="start" spacing={0}>
                          <Text fontWeight="bold" color="gray.900">
                            {currentUser.name || 'User'}
                          </Text>
                          <Text fontSize="sm" color="gray.600" textTransform="capitalize">
                            {currentUser.role}
                          </Text>
                        </VStack>
                      </HStack>
                    </Box>

                    <VStack spacing={2} align="stretch">
                      <Button
                        as={Link}
                        to="/profile"
                        variant="ghost"
                        justifyContent="flex-start"
                        leftIcon={<User size={20} />}
                        color="gray.700"
                        _hover={{ bg: 'purple.50', color: 'purple.700' }}
                        borderRadius="xl"
                        py={3}
                        px={5}
                        fontWeight="medium"
                        transition="all 0.2s ease"
                      >
                        Profile
                      </Button>
                      <Button
                        as={Link}
                        to="/settings"
                        variant="ghost"
                        justifyContent="flex-start"
                        leftIcon={<Settings size={20} />}
                        color="gray.700"
                        _hover={{ bg: 'purple.50', color: 'purple.700' }}
                        borderRadius="xl"
                        py={3}
                        px={5}
                        fontWeight="medium"
                        transition="all 0.2s ease"
                      >
                        Settings
                      </Button>
                      <Button
                        onClick={handleLogout}
                        variant="ghost"
                        justifyContent="flex-start"
                        leftIcon={<LogOut size={20} />}
                        color="red.600"
                        _hover={{ bg: 'red.50' }}
                        borderRadius="xl"
                        py={3}
                        px={5}
                        fontWeight="medium"
                        transition="all 0.2s ease"
                      >
                        Sign Out
                      </Button>
                    </VStack>
                  </>
                ) : (
                  <VStack spacing={3} align="stretch">
                    <Divider />
                    <Button
                      as={Link}
                      to="/login"
                      variant={isActiveLink('/login') ? 'solid' : 'outline'}
                      bg={isActiveLink('/login') ? gradientBg : 'purple.50'}
                      color={isActiveLink('/login') ? 'white' : 'purple.700'}
                      _hover={{
                        bg: isActiveLink('/login') ? gradientBg : 'purple.100',
                      }}
                      borderRadius="2xl"
                      fontWeight="bold"
                      py={4}
                      px={5}
                      transition="all 0.3s ease"
                    >
                      Sign In
                    </Button>
                    <Button
                      as={Link}
                      to="/register"
                      bg={gradientBg}
                      color="white"
                      _hover={{
                        bg: gradientBg,
                        transform: 'translateY(-2px)',
                      }}
                      borderRadius="2xl"
                      fontWeight="bold"
                      py={4}
                      px={5}
                      boxShadow="xl"
                      transition="all 0.3s ease"
                    >
                      Get Started Free
                    </Button>
                  </VStack>
                )}
              </VStack>
            </Container>
          </Box>
        </Collapse>
      </Box>

      {/* Spacer to prevent content from hiding behind fixed navbar */}
      <Box height={navHeight} />
    </>
  );
};

export default Navbar;
